//compounding assignment with augmented addition and substraction
 
let a = 3;
let b = 17;
let c = 99;

a += 25;
b += 9;
c -= 99;

console.log(a);
console.log(b);
console.log(c);

//compounding assignment with augmented multiplication and division

let x = 4.6;
let y = 108;

x *= 10;
y /= 12;

console.log(x);
console.log(y);